mapper.select:
select AVG(salary)
from hr
where did=#{departmentid,jdbcType=INTEGER};

select MAX(salary)
from hr
where did=#{departmentid,jdbcType=INTEGER};

select MIN(salary)
from hr
where did=#{departmentid,jdbcType=INTEGER};

select count(id)
from hr
where did=#{departmentid,jdbcType=INTEGER};

mapper.update:
update hr
set passwd = #{passwd,jdbcType=LONGVARBINARY}
where id = #{id,jdbcType=INTEGER}
